/**
 * Gets the SVG code for a given icon.
 *
 * @param string $icon is da icon.
 * @param string $icon_width is da icon width in px.
 * @param string $icon_color is da icon hex color.
 */
function canuckcp_svg( $icon, $icon_width = '16', $icon_color = '#7f7f7f' ) {
	$canuckcp_icons = canuckcp_icon_array();
	if ( '' === $icon ) {
		$icon = 'question-circle';
	}
	$icon_picked = $canuckcp_icons[ $icon ];
	if ( 'noinline' === $icon_width ) {
		$width = '<svg class="icon-svg-class"';
	} else {
		$width = '<svg class="icon-svg-class" width="' . $icon_width . '"';
	}
	$fill        = '<path class="icon-path-class ' . $icon . '" fill="' . $icon_color . '"';
	$icon_picked = str_replace( '<svg', $width, $icon_picked );
	$icon_picked = str_replace( '<path', $fill, $icon_picked );
	return $icon_picked;
}

/**
 * Gets the SVG array for use in customizer or elsewhere.
 */
function canuckcp_get_svg_array() {
	$canuckcp_icons = canuckcp_icon_array();
	return $canuckcp_icons;
}

