/**
 * Social icons â€“ svg names.
 *
 * Helper for customizer dropdown, social panel.
 */
function canuckcp_social_links() {
	$socialhtml = '';
	$social_ids = canuckcp_social_select();
	for ( $i = 1; $i < 9; $i++ ) {
		$socialname    = 'canuckcp_social_type' . $i;
		$socialurl     = 'canuckcp_social_url' . $i;
		$socialnameget = get_theme_mod( $socialname, 'donotuse' );
		$socialurlget  = get_theme_mod( $socialurl, 'donotuse' );
		if ( isset( $socialnameget ) && 'donotuse' !== $socialnameget && '' !== $socialnameget ) {
			// phpcs:ignore
			$socialhtml  .= '<a class="canuck-cp-social social-' . esc_attr( $social_ids[ $socialnameget ] ) . '" href="' . esc_url( $socialurlget ) . '" target="_blank" title="' . esc_attr( $social_ids[ $socialnameget ] ) . '">' . canuckcp_svg( $socialnameget, '16', '#eaeaea' ) . '<span class="social-screen-reader-text">' . esc_attr( $social_ids[ $socialnameget ] ) . '</span></a>';
		}
	}
	return $socialhtml;
}
